#!/bin/sh
echo "Running './configure CC=clang CFLAGS=\"-g\" CXX=clang++ CXXFLAGS=\"-g -stdlib=libc++ -std=c++1z -Weverything -Wno-c++98-compat -Wno-covered-switch-default -Wno-c++98-compat-pedantic -Wno-global-constructors -Wno-float-equal -Wno-documentation -Wno-documentation-unknown-command -Wno-padded -Wno-exit-time-destructors -Wno-reserved-id-macro\" LDFLAGS=\"-stdlib=libc++\" CPPFLAGS=\"-isystem/usr/local/include/c++/v1 -isystem/usr/lib/gcc/x86_64-linux-gnu/6/include\ -isystem/usr/lib/gcc/x86_64-linux-gnu/6/include-fixed\" --enable-xcb"
./configure CC=clang CFLAGS="-g" CXX=clang++ CXXFLAGS="-g -stdlib=libc++ -std=c++1z -Weverything -Wno-c++98-compat -Wno-covered-switch-default -Wno-c++98-compat-pedantic -Wno-global-constructors -Wno-float-equal -Wno-documentation -Wno-documentation-unknown-command -Wno-padded -Wno-exit-time-destructors -Wno-reserved-id-macro" LDFLAGS="-stdlib=libc++" CPPFLAGS="-isystem/usr/local/include/c++/v1 -isystem/usr/lib/gcc/x86_64-linux-gnu/6/include -isystem/usr/lib/gcc/x86_64-linux-gnu/6/include-fixed" --enable-xcb

